package week2lab;

public class PyramidExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i=1;i<=3;i++) {
			for (int j=1;j<=3;i++) {
				System.out.print("* "); //ln removed. printing same line
		}
		System.out.println(); // new line 
		}
	}

}
